answer:
package main

import (
	"context"
	"fmt"
	"os"

	"github.com/aws/aws-sdk-go-v2/config"
	"github.com/aws/aws-sdk-go-v2/service/s3"
)

// Creates an S3 Bucket
//
// Usage:
// go run s3_create_bucket BUCKET_NAME
func main() {
	if len(os.Args) != 2 {
		exitErrorf("Bucket name missing!\nUsage: %s bucket_name", os.Args[0])
	}

	bucket := os.Args[1]

	cfg, err := config.LoadDefaultConfig(context.TODO())
	if err != nil {
		exitErrorf("unable to load SDK config, %v", err)
	}

	// Create S3 service client
	client := s3.NewFromConfig(cfg)

	// Create the S3 Bucket
	_, err = client.CreateBucket(context.TODO(), &s3.CreateBucketInput{
		Bucket: &bucket,
	})
	if err != nil {
		exitErrorf("Unable to create bucket %q, %v", bucket, err)
	}

	// Wait until bucket is created before finishing
	fmt.Printf("Waiting for bucket %q to be created...\n", bucket)

	_, err = client.WaitUntilBucketExists(context.TODO(), &s3.HeadBucketInput{
		Bucket: &bucket,
	})
	if err != nil {
		exitErrorf("Error occurred while waiting for bucket to be created, %v", bucket)
	}

	fmt.Printf("Bucket %q successfully created\n", bucket)
}

func exitErrorf(msg string, args ...interface{}) {
	fmt.Fprintf(os.Stderr, msg+"\n", args...)
	os.Exit(1)
}

